package config

import \(
	"fmt"
	"testing"
\)

func TestDebugURLParsing\(t *testing.T\) {
	urls := []string{
		"http://localhost:9200",
		"http://user:pass@localhost:9200",
		"https://elastic:secret123@es.example.com:9200",
	}

	for _, rawURL := range urls {
		cfg, err := ParseURL\(rawURL\)
		if err != nil {
			t.Logf\("Error parsing %s: %v", rawURL, err\)
			continue
		}
		fmt.Printf\("Input:  %s\\n", rawURL\)
		fmt.Printf\("Host:   %s\\n", cfg.Host\)
		fmt.Printf\("User:   %s\\n", cfg.Username\)
		fmt.Printf\("Pass:   %s\\n", cfg.Password\)
		fmt.Printf\("Masked: %s\\n\\n", cfg.MaskedURL\(\)\)
	}
}
GOTEST)
